import React from 'react';
import { Link } from 'react-router-dom';
import {formatISO9075} from 'date-fns';
import UserProfile from './UserProfile';
import './Posts.css';

const Posts = ({title, summary, cover, content, createdAt, author}) => {
    return (
        <div className='post'>
            <div className='texts'>
                <div className='image'>
                    <Link to={'/post/id'}>
                        <img src={'http://localhost:8000/'+ cover} alt='img' />
                    </Link>
                </div>
                <div className='blog'>
                    <Link to={'/post/id'}>
                        <h2 className='blog-title'>{title}</h2>
                    </Link>
                    <p className='info'>
                        {/* <Link  to={`/userprofile/author/${author.username}`} className='author'   >{author.username}</Link> */}
                        <a href='/userprofile' className='author'>{author.username}</a>
                        <br />
                        <time>{formatISO9075(new Date(createdAt))}</time>
                    </p>
                    <p className='summary'>{summary}</p>
                </div>
            </div>
        </div>
    )
}

export default Posts
